import React from 'react'
import Header from './Header'
import { useStateValue } from './StateProvider'
import "./Checkout.css"
import CheckoutProduct from './CheckoutProduct'
function Checkout() {
    const [{basket}]=useStateValue()
    return (
        <div className="checkout">
            <Header/>
            <div>
                <img className="checkout_ad" src="https://images-na.ssl-images-amazon.com/images/G/01/ProBeautyB2B/amazon-professional-beauty-wholesale-banner2x2_sign_up_CTA.jpg" alt="banner"/>
                {basket.length===0?
                (<div>
                    <h1>Your Shopping Cart is Empty</h1>
                    <p>You have no items in your cart. visit to store and make your purchase to make it visible here.</p>
                </div>)
                :(<div>
                    <h1 className="checkout_title"> {basket?.length} Item{basket?.length?"s":""} in your Cart </h1>
                    {basket.map(item=>(
                        <CheckoutProduct 
                        item={item.id}
                        title={item.title}
                        image={item.image}
                        price={item.price}
                        rating={item.rating}
                        />
                    ))}
                </div>)}
            </div>
        </div>
    )
}

export default Checkout
