export const initialState={
    basket:[],
}

const reducer = (state,action)=>{
    console.log(action);
    switch (action.type) {
        case "ADD_TO_BASKET":
            return {
                ...state,//returning the curent stare,
                basket:[...state.basket,action.item]//adding the item to the cussr
            };
        case "REMOVE_FROM_BASKET":
            return {state}
        default:
            return state
    }
}
export default reducer
